import { useState } from 'react'
import { findAllInRenderedTree } from 'react-dom/test-utils'

function Rating() {
  const [rated, setRating] = useState(0)
  //const [hover, setHover] = useState(0)

  const changeRating = () => {
    setRating(rated + 1)

    /*if (rated !== 2) {
      setRating(rated + 1)
    } else {
      setRating(0)
    }*/
  }

  function Star(props) {
    return (
      <svg
        className={props.className}
        onClick={props.eventHandler}
        value={props.rate}
        xmlns="http://www.w3.org/2000/svg"
        width="24"
        height="24"
        viewBox="0 0 24 24"
      >
        <path
          fill="current"
          d="M12 .587l3.668 7.568 8.332 1.151-6.064 5.828 1.48 8.279-7.416-3.967-7.417 3.967 1.481-8.279-6.064-5.828 8.332-1.151z"
        />
      </svg>
    )
  }

  switch (value) {
    case 0:
      setRating(rated + 1)
      break

    case 1:
      setRating(rated + 1)
      break

    default:
      break
  }

  return (
    <div className="rating-wrapper">
      <Star
        className={`rating-element ${
          rated >= 1 ? 'rating-element__clicked' : null
        }`}
        rate={1}
        eventHandler={changeRating}
      />
      <Star
        className={`rating-element ${
          rated >= 2 ? 'rating-element__clicked' : null
        }`}
        rate={2}
        eventHandler={changeRating}
      />
      <Star
        className={`rating-element ${
          rated >= 3 ? 'rating-element__clicked' : null
        }`}
        rate={3}
        eventHandler={changeRating}
      />
      <Star
        className={`rating-element ${
          rated >= 4 ? 'rating-element__clicked' : null
        }`}
        rate={4}
        eventHandler={changeRating}
      />
      <Star
        className={`rating-element ${
          rated === 5 ? 'rating-element__clicked' : null
        }`}
        rate={5}
        eventHandler={changeRating}
      />
    </div>
  )
}

export default Rating
