import { useState } from 'react'

function Star({ active, enter, id, setRating }) {
  return (
    <svg
      className={`rating-element ${
        id <= active ? 'rating-element__clicked' : null
      }`}
      onClick={() => {
        setRating(id)
      }}
      onMouseEnter={() => {
        setRating(id)
      }}
      onMouseLeave={() => {
        if (this.state.clicked) {
          setRating(id)
        } else setRating(0)
      }}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
    >
      <path
        fill="current"
        d="M12 .587l3.668 7.568 8.332 1.151-6.064 5.828 1.48 8.279-7.416-3.967-7.417 3.967 1.481-8.279-6.064-5.828 8.332-1.151z"
      />
    </svg>
  )
}

export default Star
