import { render } from '@testing-library/react'
import { useState } from 'react'
import StarClass from './StarClass'

class RatingClass extends PureComponent {
  constructor(props) {
    super(props)
    this.state = {
      userRating: 0,
      userHover: 0,
      userSubmit: false,
      buttonText: 'Rate',
    }
  }

  render() {
    return (
      <div className="rating-wrapper">
        {[1, 2, 3, 4, 5].map((index) => (
          <Star
            id={index}
            setRating={this.setUserRating()}
            active={this.userRating}
            hover={this.userHover}
            setHover={this.setUserHover()}
            submit={this.userSubmit}
          />
        ))}
        <button
          className={`${
            this.userSubmit
              ? 'rating-button rating-button__on'
              : 'rating-button rating-button__off'
          }`}
          onClick={() => {
            this.setUserSubmit({ userSubmit: true })
            this.setButtonText({ buttonText: '✓' })
          }}
        >
          {this.buttonText}
        </button>
      </div>
    )
  }
}

export default RatingClass
