import { useState } from 'react'

const Star = (props) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    width="24"
    height="24"
    viewBox="0 0 24 24"
  >
    <path
      fill="current"
      d="M12 .587l3.668 7.568 8.332 1.151-6.064 5.828 1.48 8.279-7.416-3.967-7.417 3.967 1.481-8.279-6.064-5.828 8.332-1.151z"
    />
  </svg>
)

function Rating({ filled, onClick }) {
  const [rated, setRating] = useState(0)
  /*const [hover, setHover] = useState(0)

  const changeRate = () => {
    if (rated !== 2) {
      setRating(rated + 1)
    } else {
      setRating(0)
    }
  }*/

  return (
    <div className="rating-wrapper">
      <Star
        onClick={() => {
          fill = 'yellow'
          setRating(rated + 1)
        }}
        className={`rating-element ${
          rated === 1 ? 'rating-element__clicked' : null
        }`}
        alt="Rating Star"
      />
      {rated}
    </div>
  )
}

export default Rating
