import { ReactComponent as Star } from '../star.svg'
import { useState } from 'react'

function Rating({ filled, onClick }) {
  const [rated, setRating] = useState(0)
  /*const [hover, setHover] = useState(0)

  const changeRate = () => {
    if (rated !== 2) {
      setRating(rated + 1)
    } else {
      setRating(0)
    }
  }*/

  return (
    <div className="rating-wrapper">
      <Star
        onClick={() => {
          fill = 'yellow'
          setRating(rated + 1)
        }}
        className={`rating-element ${
          rated === 1 ? 'rating-element__clicked' : null
        }`}
        alt="Rating Star"
      />
      {rated}
    </div>
  )
}

export default Rating
