import { PureComponent, useState } from 'react'
import StarClass from './StarClass'

class RatingClass extends PureComponent {
  constructor() {
    super()
    this.state = {
      userRating: 0,
      userHover: 0,
      userSubmit: false,
      buttonText: 'Rate',
    }
  }

  render() {
    return (
      <div className="rating-wrapper">
        {[1, 2, 3, 4, 5].map((index) => (
          <StarClass
            className={`rating-element ${
              index <= (this.state.userHover || this.state.userRating)
                ? 'rating-element__focused'
                : null
            }`}
            onClick={() => {
              if (!this.state.userSubmit) {
                this.setState({ ...this.state, userRating: index })
                console.log(this.state.userRating)
              }
            }}
            onMouseEnter={() => {
              if (!this.state.userSubmit) {
                this.setState({ userHover: index })
              }
            }}
            onMouseLeave={() => {
              if (!this.state.userSubmit) {
                this.setState({ userHover: this.state.userRating })
              }
            }}
          />
        ))}
        <button
          className={`${
            this.state.userSubmit
              ? 'rating-button rating-button__on'
              : 'rating-button rating-button__off'
          }`}
          onClick={() => {
            this.setState({ userSubmit: true })
            this.setState({ buttonText: '✓' })
          }}
        >
          {this.state.buttonText}
        </button>
      </div>
    )
  }
}

export default RatingClass
